		["Effective Health - Mythic+ v1.88.5"] = {
			["outline"] = "OUTLINE",
			["disjunctive"] = "custom",
			["displayText"] = "%c",
			["customText"] = "function()\n    -- Imports reduction, absorbs and immunity status from the maih EH weakaura\n    local Import = WA_ZELER_EH\n    if not Import then return end -- To avoid lua errors\n    local ReductionP, ReductionM = Import.REDP, Import.REDM\n    local Absorbs, AbsorbsP, AbsorbsM = Import.ABS, Import.ABSP, Import.ABSM\n    local Armor = Import.EHARMOR\n    local StaggerP, StaggerM = Import.STAGGERP, Import.STAGGERM\n    aura_env.PImmunity = Import.EHPIMMUNITY or 0\n    aura_env.MImmunity = Import.EHMIMMUNITY or 0\n    \n    \n    -- Setting some aura_env to local variables to improve performance\n    local ClassID = aura_env.ClassID\n    local SpecID = aura_env.SpecID\n    local SpellName = aura_env.SpellName\n    \n    \n    -- Recalculates some variables\n    local CurrentHealth = UnitHealth(\"player\") / 10^6\n    local MaxHealth = UnitHealthMax(\"player\") / 10^6\n    local PercentHealth = CurrentHealth / MaxHealth * 100\n    \n    -- Avoidance calculation\n    local AvoidP, AvoidM, ElusiveP, ElusiveM = 1, 1, 1, 1\n    if aura_env.PAoE == 1 then\n        AvoidP = 1 - (GetAvoidance() / 100)\n        if ClassID == 6 and SpecID == 3 then AvoidP = AvoidP * 0.80 end -- Unholy Death Knight - Artifact trait\n        if UnitBuff(\"player\", SpellName(1966)) then AvoidP = AvoidP * 0.50 end -- Feint\n    elseif UnitBuff(\"player\", SpellName(1966)) and IsPlayerSpell(79008) then   -- Elusiveness\n        ElusiveP = 0.70\n    end\n    if aura_env.MAoE == 1 then\n        AvoidM = 1 - (GetAvoidance() / 100)\n        if ClassID == 6 and SpecID == 3 then AvoidM = AvoidM * 0.80 end -- Unholy Death Knight - Artifact trait\n        if UnitBuff(\"player\", SpellName(1966)) then AvoidM = AvoidM * 0.50 end -- Feint\n    elseif UnitBuff(\"player\", SpellName(1966)) and IsPlayerSpell(79008) then   -- Elusiveness\n        ElusiveM = 0.70\n    end\n    if aura_env.IgnoreArmor == 1 then\n        ReductionP = ReductionP / Armor\n    end\n    \n    ReductionP = ReductionP * AvoidP * ElusiveP\n    ReductionM = ReductionM * AvoidM * ElusiveM\n    \n    -- Get spell damage\n    local DamageSpellName = \"\"\n    local DamageP = aura_env.PSpellDamage or 0\n    local DamageM = aura_env.MSpellDamage or 0\n    \n    if WeakAuras.IsOptionsOpen() then\n        aura_env.MSpellName = \"After Hit\"\n        DamageM = 6.75\n    end\n    \n    -- Dampen Harm\n    if ClassID == 10 then\n        if UnitBuff(\"player\", SpellName(122278)) then\n            local RokmoraHits = aura_env.RokmoraHits\n            if RokmoraHits > 1 then -- Special case for the Rokmora encounter\n                local RokHitM = DamageM / (RokmoraHits - 1)\n                ReductionP = ReductionP / 0.8 / StaggerP -- Remove reductions from the base module\n                ReductionM = ReductionM / 0.8 / StaggerM\n                -- Reduction scales linearly with size of hit relative to max health\n                local HitSizeP = ReductionP * DamageP / MaxHealth\n                local HitSizeM = ReductionM * RokHitM / MaxHealth\n                if HitSizeP > 1 then HitSizeP = 1 end\n                if HitSizeM > 1 then HitSizeM = 1 end\n                local DampenHarmP = 0.8 - 0.3 * HitSizeP\n                local DampenHarmM = 0.8 - 0.3 * HitSizeM\n                ReductionP = ReductionP * DampenHarmP * StaggerP\n                ReductionM = ReductionM * DampenHarmM * StaggerM\n            else\n                ReductionP = ReductionP / 0.8 / StaggerP -- Remove reductions from the base module\n                ReductionM = ReductionM / 0.8 / StaggerM\n                -- Reduction scales linearly with size of hit relative to max health\n                local HitSizeP = ReductionP * DamageP / MaxHealth\n                local HitSizeM = ReductionM * DamageM / MaxHealth\n                if HitSizeP > 1 then HitSizeP = 1 end\n                if HitSizeM > 1 then HitSizeM = 1 end\n                local DampenHarmP = 0.8 - 0.3 * HitSizeP\n                local DampenHarmM = 0.8 - 0.3 * HitSizeM\n                ReductionP = ReductionP * DampenHarmP * StaggerP\n                ReductionM = ReductionM * DampenHarmM * StaggerM\n            end\n        end\n    end\n    \n    -- Spectral Deflection\n    if ClassID == 6 then\n        if IsPlayerSpell(211078) then\n            if aura_env.RokmoraHits > 1 then -- Special case for the Rokmora encounter\n                local Stack = select(4, UnitBuff(\"player\", SpellName(195181))) or 0 -- Bone Shield stacks\n                local HitSizeP = ReductionP * DamageP / MaxHealth * 100\n                if Stack > 1 and HitSizeP > 25 then\n                    ReductionP = ReductionP / 0.84 -- Remove damage reduction from Bone Shield\n                    ReductionP = ReductionP * 0.68 -- Add damage reduction from Spectral Deflection\n                end\n            else\n                local Stack = select(4, UnitBuff(\"player\", SpellName(195181))) or 0 -- Bone Shield stacks\n                local HitSizeP = ReductionP * DamageP / MaxHealth * 100\n                local HitSizeM = ReductionM * DamageM / MaxHealth * 100\n                if Stack > 1 and HitSizeP > 25 then\n                    ReductionP = ReductionP / 0.84 -- Remove damage reduction from Bone Shield\n                    ReductionP = ReductionP * 0.68 -- Add damage reduction from Spectral Deflection\n                end\n                if Stack > 1 and HitSizeM > 25 then\n                    ReductionM = ReductionM / 0.84 -- Remove damage reduction from Bone Shield\n                    ReductionM = ReductionM * 0.68 -- Add damage reduction from Spectral Deflection\n                end\n            end\n        end\n    end\n    \n    \n    -- Calculate remaining health\n    DamageP = DamageP * ReductionP - (Absorbs - AbsorbsM)\n    if DamageP < 0 then DamageP = 0 end\n    DamageM = DamageM * ReductionM - (Absorbs - AbsorbsP)\n    if DamageM < 0 then DamageM = 0 end\n    Absorbs = AbsorbsP + AbsorbsM - Absorbs\n    local HPleft = (CurrentHealth + Absorbs - DamageP - DamageM) / MaxHealth * 100\n    if HPleft > PercentHealth then HPleft = PercentHealth end\n    HPleft = aura_env.Color(HPleft)\n    \n    -- Add color to spellnames\n    -- For now, magic spellnames take priority on display\n    if aura_env.PSpellName ~= \"\" then\n        DamageSpellName = \"|cFFFFAF50\" .. aura_env.PSpellName .. \"|r\"\n    end\n    if aura_env.MSpellName ~= \"\" then\n        DamageSpellName = \"|cFF197DFF\" .. aura_env.MSpellName .. \"|r\"\n    end\n    \n    return DamageSpellName .. HPleft\nend",
