				["custom"] = "function()\n    -- Reset to default values\n    aura_env.PSpellDamage = 0\n    aura_env.PSpellName = \"\"\n    aura_env.PAoE = 0\n    aura_env.IgnoreArmor = 0\n    \n    aura_env.MSpellDamage = 0\n    aura_env.MSpellName = \"\"\n    aura_env.MAoE = 0\n    aura_env.RokmoraHits = 1\n    \n    local unit = \"target\"\n    if UnitExists(\"boss1\") then unit = \"boss1\" end\n    local Multiplier = 1\n    \n    -- Prioritize boss if pulled\n    local unit = \"target\"\n    if UnitExists(\"boss1\") then unit = \"boss1\" end\n    if UnitExists(unit) then\n        -- Check for buffs on selected unit\n        local n = 1\n        local _,_,_,Stacks,_,_,_,_,_,_,BuffID = UnitBuff(unit, n)\n        while BuffID do\n            if BuffID == 206150 then -- Mythic+ damage multiplier\n                Multiplier = Multiplier * (1 + (select(18, UnitBuff(unit, n)) or 0) / 100)\n            end\n            if BuffID == 209859 then -- Bolster Affix, buff\n                Multiplier = Multiplier * 1.2\n            end\n            if BuffID == 228318 then -- Enrage Affix, buff\n                Multiplier = Multiplier * 2\n            end\n            if BuffID == 228895 then -- Kara, Midnight - Enrage\n                Multiplier = Multiplier * 2\n            end\n            if aura_env.DungeonSpellID[BuffID] then\n                Multiplier = Multiplier * (1 + Stacks * aura_env.DungeonSpellID[BuffID])\n            end\n            n = n + 1\n            _,_,_,Stacks,_,_,_,_,_,_,BuffID = UnitBuff(unit, n)\n        end\n        \n        -- Maddening Roar debuff\n        local RoarStacks = select(4, UnitDebuff(\"player\", aura_env.DungeonSpellID[200580])) or 0\n        local Roar = 1 + ((RoarStacks * 50) / 100)\n        \n        -- Thunder buff, Hyrja (debuff on boss)\n        local ThunderStacks = select(4, UnitDebuff(\"boss1\", aura_env.DungeonSpellID[192132])) or 0\n        local Thunder = 1 + ((ThunderStacks * 10) / 100)\n        \n        -- CoS, Gereth the Vile - Shadow Bolt Volley debuff\n        if UnitDebuff(\"player\", aura_env.SpellName(214692)) then\n            Multiplier = Multiplier * 1.15\n        end\n        \n        -- Get NpcID\n        local NpcGUID = UnitGUID(unit)\n        local NpcID = select(6, strsplit(\"-\", NpcGUID))\n        NpcID = tonumber(NpcID)\n        \n        -- Checks physical spell\n        local DungeonID = aura_env.PDungeon\n        if DungeonID[NpcID] then\n            aura_env.PSpellDamage = (DungeonID[NpcID][1] or 0) * Multiplier\n            aura_env.PSpellName = DungeonID[NpcID][2] or \"\"\n            aura_env.PAoE = DungeonID[NpcID][3] or 0\n            aura_env.IgnoreArmor = DungeonID[NpcID][5] or 0\n        end\n        \n        -- Checks magic spell\n        DungeonID = aura_env.MDungeon\n        if DungeonID[NpcID] then\n            aura_env.MSpellDamage = (DungeonID[NpcID][1] or 0) * Multiplier * Roar * Thunder\n            aura_env.MSpellName = DungeonID[NpcID][2] or \"\"\n            aura_env.MAoE = DungeonID[NpcID][3] or 0\n        end\n        \n        -- Override for Xavius' AoE\n        if NpcID == 99192 then\n            local XaviusHealth = (UnitHealth(unit) / UnitHealthMax(unit)) * 100 -- Percent HP for Xavius\n            local now = GetTime()\n            if XaviusHealth <= 50.5 and XaviusHealth > 45 and (now - aura_env.ShadeOfXaviusTime) > 60 then\n                -- Seems to start casting as soon as he hits 50.5% hp\n                aura_env.ShadeOfXaviusTime = now\n            end\n            if (now - aura_env.ShadeOfXaviusTime) < 6.5 then\n                aura_env.MSpellDamage = 1.329 * Multiplier -- Damage for the AoE\n                aura_env.MSpellName = \"AoE\"\n                aura_env.MAoE = 1\n            end\n        end\n        \n        -- Rokmora add shatter\n        if NpcID == 91003 then\n            local HPMultiplierBoss = 1 + (select(17, UnitBuff(\"boss1\", aura_env.SpellName(206150))) or 0) / 100\n            if HPMultiplierBoss > Multiplier then -- If Tyrannical is found, then remove it from the damage multiplier\n                Multiplier = Multiplier / 1.15\n            end\n            local AddHP = 0\n            local i = 1\n            local RokmoraHits = 1\n            local nameplate = \"nameplate\" .. i\n            for i = 1, 40 do\n                local unit = \"nameplate\" .. i\n                if UnitExists(unit) then\n                    local UG = UnitGUID(unit)\n                    local npc_id = select(6, strsplit(\"-\", UG))\n                    if npc_id == \"97720\" then -- Blightshard Skitter\n                        AddHP = AddHP + UnitHealth(unit) / UnitHealthMax(unit)\n                        RokmoraHits = RokmoraHits + 1\n                    end\n                end\n            end\n            aura_env.MSpellDamage = 0.429207 * AddHP * Multiplier\n            aura_env.MSpellName = \"Shatter\"\n            aura_env.MAoE = 1\n            aura_env.PAoE = 1\n            aura_env.RokmoraHits = RokmoraHits\n        end\n    end\n    if aura_env.PSpellDamage > 0 or aura_env.MSpellDamage > 0 then\n        if aura_env.PImmunity == 1 then aura_env.PSpellDamage = 0 end\n        if aura_env.MImmunity == 1 then aura_env.MSpellDamage = 0 end\n        return true\n    else return false\n    end\n    return false\nend",
