				["finish"] = {
				},
				["init"] = {
					["custom"] = "-- ################ Settings\naura_env.fontType = \"NumberFontNormalHuge\"\naura_env.fontTextHeight = 20\n-- ################ Dont touch anything below if you dont know what you are doing\n\n\naura_env.empowering = select(1, GetSpellInfo(228111)) \n\n-- Create button and fill WA region\naura_env.button = CreateFrame(\"Button\", \"UseArtifactButton\", WeakAuras.regions[aura_env.id].region, \"SecureActionButtonTemplate\")\naura_env.button:SetAllPoints(WeakAuras.regions[aura_env.id].region)\n\n\n\n-- AP Value stuff\naura_env.fontStr = aura_env.button:CreateFontString(\"UseArtifactButtonFrontString\", \"OVERLAY\", aura_env.fontType)\naura_env.fontStr:ClearAllPoints()\n--aura_env.fontStr:SetTextColor(1,.5,0,1) --leg color\naura_env.fontStr:SetTextColor(0.85, 0.85, 0.85, 1) --white color\naura_env.fontStr:SetParent(aura_env.button)\naura_env.fontStr:SetTextHeight(aura_env.fontTextHeight)\n-- Align top of the icon, follows other reagion\naura_env.fontStr:SetPoint(\"TOP\",WeakAuras.regions[aura_env.id].region,\"CENTER\",0,70)\n\n-- Give me a human readable string\naura_env.readableNumber = function(num, places)\n    local ret\n    local placeValue = (\"%%.%df\"):format(places or 0)\n    if not num then\n        return 0\n    elseif num >= 1000000000000 then\n        ret = placeValue:format(num / 1000000000000) .. \" Tril\" -- trillion\n    elseif num >= 1000000000 then\n        ret = placeValue:format(num / 1000000000) .. \" Bil\" -- billion\n    elseif num >= 1000000 then\n        ret = placeValue:format(num / 1000000) .. \" Mil\" -- million\n    elseif num >= 1000 then\n        ret = placeValue:format(num / 1000) .. \"k\" -- thousand\n    else\n        ret = num -- hundreds\n    end\n    return ret\nend\n\n-- From ElvUI code, Artifact.lua\n-- Just needed some minor changes to work in this context\ntooltipLines = {}\nfor i = 1, 5 do\n    tooltipLines[i] = string.format(\"APVALTooltipTextLeft%d\", i)\nend\n\nlocal apStringValueMillion = {\n    [\"enUS\"] = \"(%d*[%p%s]?%d+) million\",\n    [\"enGB\"] = \"(%d*[%p%s]?%d+) million\",\n    [\"ptBR\"] = \"(%d*[%p%s]?%d+) [[milhão][milhões]]?\",\n    [\"esMX\"] = \"(%d*[%p%s]?%d+) [[millón][millones]]?\",\n    [\"deDE\"] = \"(%d*[%p%s]?%d+) [[Million][Millionen]]?\",\n    [\"esES\"] = \"(%d*[%p%s]?%d+) [[millón][millones]]?\",\n    [\"frFR\"] = \"(%d*[%p%s]?%d+) [[million][millions]]?\",\n    [\"itIT\"] = \"(%d*[%p%s]?%d+) [[milione][milioni]]?\",\n    [\"ruRU\"] = \"(%d*[%p%s]?%d+) млн\",\n    [\"koKR\"] = \"(%d*[%p%s]?%d+)만\",\n    [\"zhTW\"] = \"(%d*[%p%s]?%d+)萬\",\n    [\"zhCN\"] = \"(%d*[%p%s]?%d+)万\",\n}\napStringValueMillionLocal = apStringValueMillion[GetLocale()]\n\n--AP item caches\nlocal apValueCache = {}\nlocal apItemCache = {}\n\n--This function scans the tooltip of an item to determine whether or not it grants AP.\n--If it is found to grant AP, then the value is extracted and returned.\nfunction GetAPFromTooltip(itemLink)\n    local apValue = 0\n    \n    local itemSpell = GetItemSpell(itemLink)\n    if itemSpell then\n        local f = CreateFrame('GameTooltip', 'APVALTooltip', UIParent, 'GameTooltipTemplate')\n        f:SetOwner(UIParent, 'ANCHOR_NONE')\n        f:SetHyperlink(itemLink)\n        \n        local apFound\n        for i = table.getn(tooltipLines)-1, 1, -1 do\n            local tooltipText = nil\n            tooltipText = _G[tooltipLines[i]]:GetText()\n            \n            if (tooltipText) then\n                local digit1, digit2, digit3, ap\n                local value = strmatch(tooltipText, apStringValueMillionLocal)\n                \n                if (value) then\n                    digit1, digit2 = strmatch(value, \"(%d+)[%p%s](%d+)\")\n                    if (digit1 and digit2) then\n                        ap = tonumber(format(\"%s.%s\", digit1, digit2)) * 1e6 --Multiply by one million\n                    else\n                        ap = tonumber(value) * 1e6 --Multiply by one million\n                    end \n                else\n                    digit1, digit2, digit3 = strmatch(tooltipText,\"(%d+)[%p%s]?(%d+)[%p%s]?(%d+)\")\n                    ap = tonumber(format(\"%s%s%s\", digit1 or \"\", digit2 or \"\", (digit2 and digit3) and digit3 or \"\"))\n                end\n                \n                if (ap) then\n                    apValue = ap\n                    apFound = true\n                    break\n                end\n            end\n        end\n        \n        if (not apFound) then\n            apItemCache[itemLink] = false --Cache item as not granting AP\n        end\n    else\n        apItemCache[itemLink] = false --Cache item as not granting AP\n    end\n    \n    return apValue\nend\n\n--This function is responsible for retrieving the AP value from an itemLink.\n--It will cache the itemLink and respective AP value for future requests, thus saving CPU resources.\naura_env.getAPForItem = function(itemLink)\n    if (apItemCache[itemLink] == false) then\n        --Get out early if item has already been determined to not grant AP\n        return 0\n    end\n    \n    --Check if item is cached and return value\n    if apValueCache[itemLink] then\n        return apValueCache[itemLink]\n    else\n        --Not cached, do a tooltip scan and cache the value\n        local apValue = GetAPFromTooltip(itemLink)\n        if apValue > 0 then\n            apValueCache[itemLink] = apValue\n        end\n        return apValue\n    end\nend",
