				["custom"] = "function ()\n    for bag = 0, NUM_BAG_SLOTS do        \n        for slot = 1, GetContainerNumSlots(bag) do            \n            local itemId = GetContainerItemID(bag, slot)            \n            local itemSpell = GetItemSpell(itemId)\n            \n            if itemSpell and itemSpell == aura_env.empowering then\n                local sName, sLink = GetItemInfo(itemId)\n                \n                -- Clear previous text\n                aura_env.fontStr:SetText(\"\")\n                \n                -- set texture to match item's icon                \n                local icon = GetItemIcon(itemId) \n                WeakAuras.regions[aura_env.id].region:SetTexture(icon)\n                \n                -- Make button clickable to use item                \n                aura_env.button:Show() \n                aura_env.button:SetAttribute(\"type\", \"item\")\n                aura_env.button:SetAttribute(\"item\", \"item:\"..itemId) \n                \n                \n                -- Add AP Text \"on icon\"\n                aura_env.fontStr:Show()\n                aura_env.fontStr:ClearAllPoints()\n                -- Align top of the icon, follows other reagion\n                aura_env.fontStr:SetPoint(\"TOP\",WeakAuras.regions[aura_env.id].region,\"CENTER\",0,70)\n                aura_env.fontStr:SetText(aura_env.readableNumber(aura_env.getAPForItem(sLink),1))\n                \n                return true\n                \n            end            \n        end\n    end\n    \n    \n    WeakAuras.regions[aura_env.id].region:SetTexture(nil)\n    \n    aura_env.button:SetAttribute(\"type\", nil)\n    aura_env.button:SetAttribute(\"item\", nil) \n    aura_env.button:Hide()\n    \n    aura_env.fontStr:Hide()\n    aura_env.fontStr:SetText(\"\")\n    \nend",
