		["Effective Health - Magic and Physical v1.88.5"] = {
			["outline"] = "OUTLINE",
			["disjunctive"] = "custom",
			["displayText"] = "%c",
			["customText"] = "function()\n    local start -- CPU benchmarking\n    local aura_env = aura_env\n    local Debug = aura_env.Debug\n    if Debug then\n        aura_env.count = aura_env.count or 1\n        aura_env.count = (aura_env.count+1)%600\n        start = debugprofilestop()\n    end\n    \n    -- Setting some aura_env to local variables to improve performance\n    local ClassID = aura_env.ClassID\n    local SpellName = aura_env.SpellName\n    local SpecID = aura_env.SpecID\n    local ArtifactTraits = aura_env.ArtifactTraits\n    local AoE = aura_env.AoE\n    local Mode = aura_env.Mode\n    local ColorFunction = aura_env.Color\n    local Threshold = aura_env.Threshold\n    \n    \n    -- Armor calulation\n    local Armor = select(2, UnitArmor(\"player\")) or 0\n    local Armor_DR = Armor / (Armor + aura_env.ArmorConst[aura_env.EnemyLevel]) or 0 -- Calculate damage reduction from armor\n    local cap = 0.85 -- Armor is capped at 85% physical reduction\n    if Armor_DR >= cap then Armor_DR = cap end\n    Armor = 1 - Armor_DR\n    \n    -- All Classes\n    local CurrentHealth = UnitHealth(\"player\") / 10^6\n    local MaxHealth = UnitHealthMax(\"player\") / 10^6\n    local Vers = 1 - (GetCombatRatingBonus(31) / 100)\n    local Avoid = 1\n    if AoE then Avoid = 1 - (GetAvoidance() / 100)\n    end\n    local Absorbs = UnitGetTotalAbsorbs(\"player\") / 10^6\n    local AbsorbsP, AbsorbsM = Absorbs, Absorbs\n    \n    -- Initialize some variables\n    local ClassDRP, ClassDRM = 1, 1\n    local PImmunity, MImmunity = 0, 0\n    \n    -- General damage reduction buffs\n    local n = 1\n    local BuffID = select(11, UnitBuff(\"player\", n))\n    while BuffID do\n        local Reduction = aura_env.Reductions[BuffID] or {1, 1}\n        local IronB = 1\n        if BuffID == 102342 then\n            IronB = 1 + (tonumber(select(17, UnitBuff(\"player\", n)) or 0)) / 100 end\n        ClassDRP = ClassDRP * Reduction[1] * IronB\n        ClassDRM = ClassDRM * Reduction[2] * IronB\n        n = n + 1\n        BuffID = select(11, UnitBuff(\"player\", n))\n    end\n    \n    \n    ----- Damage reductions with special calculations -----\n    \n    --- Death Knight ---\n    if ClassID == 6 then\n        local IceF, Necropolis, EHpet, AMS, BloodS = 1, 1, 0, 0\n        IceF = 1 + (tonumber(select(19, UnitBuff(\"player\", SpellName(48792))) or 0)) / 100 -- Icebound Fortitude // using plus since the tooltip returns a negative value\n        if IsPlayerSpell(206967) then -- Will of the Necropolis - Talent\n            if CurrentHealth / MaxHealth < 0.35 then Necropolis = 0.65\n            end\n        end\n        if AoE and SpecID == 3 then\n            local UnholyDK = 1 - 0.05 * ArtifactTraits[191584]\n            Avoid = Avoid * UnholyDK -- Unholy Artifact - Artifact trait\n        end\n        if UnitBuff(\"player\", SpellName(207319)) then -- Corpse Shield\n            local PetHealth = UnitHealth(\"pet\")\n            if UnitBuff(\"pet\", SpellName(47484)) or UnitBuff(\"pet\", SpellName(212384)) -- Huddle and Protective Bile\n            then EHpet = (PetHealth / 0.50) / 10^6 else EHpet = PetHealth / 10^6 end\n        end\n        AMS = (select(17, UnitBuff(\"player\", SpellName(48707))) or 0) / 10^6 -- Anti-Magic Shell\n        BloodS = (select(17, UnitBuff(\"player\", SpellName(77535))) or 0) / 10^6 -- Blood Shield\n        \n        Absorbs = Absorbs + EHpet\n        AbsorbsP = Absorbs - AMS\n        AbsorbsM = Absorbs - BloodS\n        ClassDRP = ClassDRP * IceF * Necropolis\n        ClassDRM = ClassDRM * IceF * Necropolis\n    end\n    \n    \n    --- Demon Hunter ---\n    if ClassID == 12 then\n        local Blur, DHmagic, DemWards, DemonS, FireBrand, PainBringer, LegCloak = 1, 1, 1, 1, 1, 1, 1\n        if IsEquippedItem(137066) and UnitBuff(\"player\", SpellName(178740)) then LegCloak = 0.85 -- Immolation Aura\n        end\n        if IsPlayerSpell(205411) and UnitBuff(\"player\", SpellName(198589)) then Blur = 0.50 -- Blur\n        elseif UnitBuff(\"player\", SpellName(198589)) then Blur = 0.65\n        end\n        if SpecID == 1 then DHmagic = 0.84 -- Artifact trait\n        end\n        if SpecID == 2 then DemWards = 0.80 -- Demonic Wards - Passive\n        end\n        DemonS = 1 + (tonumber(select(18, UnitBuff(\"player\", SpellName(203720))) or 0)) / 100 -- Demon Spikes // using plus since the tooltip returns a negative value\n        if select(4, UnitDebuff(\"target\", SpellName(204021), nil, \"player\")) then FireBrand = 0.60 -- Fiery Brand\n        end\n        PainBringer = 1 - 0.03 * (select(4, UnitBuff(\"player\", SpellName(212988))) or 0) -- Painbringer\n        if UnitBuff(\"player\", SpellName(196555)) then PImmunity = 1 -- Netherwalk\n        end\n        \n        ClassDRP = ClassDRP * DemonS * DemWards * Blur * FireBrand * PainBringer\n        ClassDRM = ClassDRM * Blur * DHmagic * DemWards * FireBrand * PainBringer * LegCloak\n    end\n    \n    \n    --- Druid ---\n    if ClassID == 11 then\n        local GuardA, Moonfire, RendandTear, Survival = 1, 1, 1, 1\n        if IsEquippedItem(137015) and IsPlayerSpell(16931) then GuardA = 0.895 elseif IsPlayerSpell(16931) then\n            GuardA = 0.94 -- Thick Hide (Guardian Affinity)\n        end\n        if SpecID == 3 then\n            if UnitDebuff(\"target\", SpellName(164812), nil, \"player\") then\n                Moonfire = 1 - 0.01 * ArtifactTraits[238049] -- Scintillating Moonlight - Artifact trait\n            end\n            if IsPlayerSpell(204053) then\n                RendandTear = 1 - 0.02 * (select(4, UnitDebuff(\"target\", SpellName(192090), nil, \"player\")) or 0) -- Rend and Tear - Talent\n            end\n            if UnitBuff(\"player\", SpellName(61336)) then\n                Survival = 0.5 - (0.10 / 3 * ArtifactTraits[200415]) -- Survival Instincts\n            end\n        else\n            if UnitBuff(\"player\", SpellName(61336)) then Survival = 0.5 end\n        end\n        \n        ClassDRP = ClassDRP * GuardA * Moonfire * RendandTear * Survival\n        ClassDRM = ClassDRM * GuardA * Moonfire * RendandTear * Survival\n    end\n    \n    \n    --- Hunter ---\n    if ClassID == 3 then\n        if UnitBuff(\"player\", SpellName(186265)) then -- Aspect of the Turtle\n            PImmunity = 1\n            MImmunity = 1\n        end\n        ClassDRP = ClassDRP\n        ClassDRM = ClassDRM\n    end\n    \n    \n    --- Mage ---\n    if ClassID == 8 then\n        if UnitBuff(\"player\", SpellName(45438)) then -- Ice block\n            PImmunity = 1\n            MImmunity = 1\n        end\n        ClassDRP = ClassDRP\n        ClassDRM = ClassDRM\n    end\n    \n    \n    --- Monk ---\n    local StaggerP, StaggerM = 1, 1\n    if ClassID == 10 then\n        local BreathFire, Karma = 1, 0\n        if SpecID == 1 then\n            BreathFire = 1 + 0.01 * (select(18, UnitDebuff(\"target\", SpellName(123725), nil, \"player\")) or 0) -- Breath of Fire\n            StaggerP = 0.40\n            if IsPlayerSpell(196737) then StaggerP = StaggerP + 0.1 end -- High Tolerance\n            if UnitBuff(\"player\", SpellName(120954)) then StaggerP = StaggerP + 0.10 end -- Fortifying Brew\n            StaggerP = StaggerP + (select(17, UnitBuff(\"player\", SpellName(215479))) or 0) / 100 -- Ironskin Brew\n            if IsPlayerSpell(237076) then StaggerM = 0.56 * StaggerP else StaggerM = 0.40 * StaggerP end\n            StaggerP = 1 - StaggerP\n            StaggerM = 1 - StaggerM\n            \n            -- Stagger cap\n            local Stagger = UnitStagger(\"player\") or 0 -- Calculate MaxHealth effective reduction for Stagger\n            local StaggerCapP = (CurrentHealth + AbsorbsP) / (CurrentHealth + AbsorbsP + 10 * MaxHealth - Stagger)\n            local StaggerCapM = (CurrentHealth + AbsorbsM) / (CurrentHealth + AbsorbsM + 10 * MaxHealth - Stagger)\n            if StaggerCapP < 0.01 then StaggerCapP = 0.01 end -- Stagger caps at 99%\n            if StaggerCapM < 0.01 then StaggerCapM = 0.01 end\n            if StaggerP < StaggerCapP then StaggerP = StaggerCapP end\n            if StaggerM < StaggerCapM then StaggerM = StaggerCapM end\n        end\n        if SpecID == 3 then\n            Karma = aura_env.TouchOfKarma / 10^6\n            Absorbs = Absorbs + Karma\n            AbsorbsP = AbsorbsP + Karma\n            AbsorbsM = AbsorbsM + Karma\n        end\n        \n        ClassDRP = ClassDRP * BreathFire * StaggerP\n        ClassDRM = ClassDRM * BreathFire * StaggerM\n    end\n    \n    \n    --- Paladin ---\n    local DevotionAura = 1 + (tonumber(select(17, UnitBuff(\"player\", SpellName(210320))) or 0)) / 100 -- Devotion Aura // using plus since the tooltip returns a negative value\n    if UnitBuff(\"player\", SpellName(1022)) then PImmunity = 1 end -- Blessing of Protection\n    if UnitBuff(\"player\", SpellName(204018)) then MImmunity = 1 end -- Blessing of Spellwarding\n    if ClassID == 2 then\n        local EyeTyr, KnightTemp, ShieldR, PalaRing = 1, 1, 1, 1\n        if select(8, UnitDebuff(\"target\", SpellName(209202))) == \"player\" then EyeTyr = 0.75 -- Eye of Tyr\n        end\n        if IsPlayerSpell(204139) and UnitBuff(\"player\", SpellName(190784)) then KnightTemp = 0.80 -- Knight Templar and Divine Steed\n        end\n        local ShieldR = 1 + (tonumber(select(17, UnitBuff(\"player\", SpellName(53600))) or 0)) / 100 -- Shield of the Righteous // using plus since the tooltip returns a negative value\n        if IsEquippedItem(137047) and UnitBuff(\"player\", SpellName(26573)) then PalaRing = 0.96 -- Consecration\n        end\n        if UnitBuff(\"player\", SpellName(642)) then -- Divine Shield\n            PImmunity = 1\n            MImmunity = 1\n        end\n        \n        ClassDRP = ClassDRP * EyeTyr * KnightTemp * ShieldR * PalaRing\n        ClassDRM = ClassDRM * EyeTyr * KnightTemp * ShieldR * PalaRing\n    end\n    \n    \n    --- Priest ---\n    if ClassID == 5 then\n        local FocusedW, Perseverance = 