ation()\n\nfunction aura_env.GetArtifactTraitRank(traitid)\n    local CurrentRank = 4 -- Defaults rank is 4\n    if IsAddOnLoaded(\"LibArtifactData-1.0\") or LoadAddOn(\"LibArtifactData-1.0\") then\n        aura_env.LAD = aura_env.LAD or LibStub(\"LibArtifactData-1.0\")\n        if not aura_env.LAD:GetActiveArtifactID() then\n            aura_env.LAD:ForceUpdate()\n        end\n        -- pos, traitid, spellid, name, icon, rank, max, bonus, gold, first, last\n        for _, _, spellid, name, _, rank in aura_env.LAD:IterateTraits() do\n            if spellid == traitid then\n                CurrentRank = rank\n                break\n            end\n        end\n    end\n    return CurrentRank\nend\n\naura_env.ArtifactTraits = {\n    -- Unholy Death Knight --\n    [191584] = aura_env.GetArtifactTraitRank(191584), -- Unholy Endurance\n    -- Guardian Druid --\n    [238049] = aura_env.GetArtifactTraitRank(238049), -- Scintillating Moonlight\n    [200415] = aura_env.GetArtifactTraitRank(200415), -- Survival Instincts\n    -- Mistweaver Monk --\n    [199365] = aura_env.GetArtifactTraitRank(199365), -- Shroud of Mist\n    -- Marksmanship Hunter --\n    [190514] = aura_env.GetArtifactTraitRank(190514), -- Survival of the Fittest\n    -- Holy Paladin --\n    [200302] = aura_env.GetArtifactTraitRank(200302), -- Knight of the Silver Hand\n    -- Discipline Priest --\n    [197711] = aura_env.GetArtifactTraitRank(197711), -- Vestments of Discipline\n    -- Restoration Shaman --\n    [207354] = aura_env.GetArtifactTraitRank(207354), -- Caress of the Tidemother\n    [207351] = aura_env.GetArtifactTraitRank(207351), -- Ghost in the Mists\n    -- Destruction Warlock --\n    [196305] = aura_env.GetArtifactTraitRank(196305), -- Eternal Struggle\n}\n\n-- Class self-buffs: [Spell ID] = {Phys Reduction, Magic Reduction}\naura_env.Reductions = {\n    \n    --- Death Knight ---\n    [195181] = {0.84, 0.84}, -- Bone Shield\n    [194679] = {0.60, 0.60}, -- Rune Tap\n    \n    --- Demon Hunt