			["displayText"] = "%c",
			["customText"] = "function()\n    local Import = WA_ZELER_EH\n    local aura_env = aura_env\n    if not Import then return end -- To avoid lua errors\n    local ReductionP, ReductionM = Import.REDP, Import.REDM\n    -- local EHP, EHM = Import.EHP, Import.EHM\n    -- local EHPMax, EHMMax = Import.EHPMAX, Import.EHMMAX\n    local Absorbs, AbsorbsP, AbsorbsM = Import.ABS, Import.ABSP, Import.ABSM\n    local Armor = Import.EHARMOR\n    local PImmunity, MImmunity = Import.EHPIMMUNITY, Import.EHMIMMUNITY\n    local StaggerP, StaggerM = Import.STAGGERP, Import.STAGGERM\n    \n    \n    -- Setting some aura_env to local variables to improve performance\n    local EncounterID = aura_env.EncounterID\n    local SpellName = aura_env.SpellName\n    local RaidDifficulty = aura_env.RaidDifficulty\n    local Color = aura_env.Color\n    local ComboStart = aura_env.ComboStart\n    local ClassID = aura_env.ClassID\n    \n    \n    -- Disable for Paladins and Warriors until block calculation is implemented\n    if ClassID == 1 or ClassID == 2 then return end\n    \n    \n    -- Initialize local variables\n    local CurrentHealth = UnitHealth(\"player\") / 10^6\n    local MaxHealth = UnitHealthMax(\"player\") / 10^6\n    local EHP, EHM = CurrentHealth + AbsorbsP / ReductionP, CurrentHealth + AbsorbsM / ReductionM\n    local EHPMax, EHMMax = MaxHealth + AbsorbsP / ReductionP, MaxHealth + AbsorbsM / ReductionM\n    local AntorusP, AntorusM, BigHitP, BigHitM = 1, 1, 0, 0\n    local Avoid = 1\n    local AfterHitP, AfterHitM = \" \", \" \"\n    local SpellNameP = \"After Hit\"\n    local SpellNameM = \"After Hit\"\n    \n    \n    if WeakAuras.IsOptionsOpen() then\n        BigHitP = 43\n        BigHitM = 12.5\n        Avoid = 1 - (GetAvoidance() / 100)\n        SpellNameP = SpellName(244291) -- Foe Breaker\n        SpellNameM = SpellName(243960) -- Shadow Strike\n    end\n    \n    \n    --- Fel Hounds ---\n    if EncounterID == 2074 then\n        local Shadowtouched, Flametouched, SargBlessing = 1, 1, 1\n        SpellNameM = SpellName(254760) -- Corrupting Maw\n        if RaidDifficulty == 14 then BigHitM = 4.235     -- Normal\n        elseif RaidDifficulty == 15 then BigHitM = 5.925 -- Heroic\n        elseif RaidDifficulty == 16 then BigHitM = 8     -- Mythic\n        end\n        if UnitDebuff(\"player\", SpellName(244054)) and UnitIsUnit(\"boss1target\", \"player\") then -- Flametouched\n        Flametouched = 0.75 end\n        if UnitDebuff(\"player\", SpellName(244055)) and UnitIsUnit(\"boss2target\", \"player\") then -- Shadowtouched\n        Shadowtouched = 0.75 end\n        if UnitBuff(\"boss1\", SpellName(246057)) then -- Sargeras' Blessing\n        SargBlessing = 2 end\n        BigHitM = BigHitM * Shadowtouched * Flametouched * SargBlessing\n    end\n    \n    \n    --- Antoran High Command ---\n    if EncounterID == 2070 then\n        local Exploited = 1\n        SpellNameP = SpellName(244892) -- Exploit Weakness\n        local ExploitedT = select(4, UnitDebuff(\"player\", SpellName(244892))) or 0\n        if UnitIsUnit(\"boss1target\", \"player\") or UnitIsUnit(\"boss2target\", \"player\") or UnitIsUnit(\"boss3target\", \"player\") then\n            Exploited = 1 + (ExploitedT * 50) / 100\n            if RaidDifficulty == 14 then BigHitP = 2.855   -- Normal\n            elseif RaidDifficulty == 15 then BigHitP = 4   -- Heroic\n            elseif RaidDifficulty == 16 then BigHitP = 7.5 -- Mythic\n            end\n        end\n        BigHitP = BigHitP * Exploited\n    end\n    \n    \n    --- Imonar ---\n    if EncounterID == 2082 then\n        local ShockLanced, EMPShockLanced, Shocked, Seared = 1, 1, 1, 1\n        SpellNameP = SpellName(247367) -- Shock Lance\n        SpellNameM = SpellName(247681) -- Pulse Grenade\n        -- Shock Lance\n        local ShockLancedT = select(4, UnitDebuff(\"player\", SpellName(247367))) or 0\n        if ShockLancedT > 0 and UnitIsUnit(\"boss1target\", \"player\") then\n            ShockLanced = 1 + (ShockLancedT * 15) / 100\n            if RaidDifficulty == 14 then BigHitP = 3.27      -- Normal\n            elseif RaidDifficulty == 15 then BigHitP = 4.575 -- Heroic\n            elseif RaidDifficulty == 16 then BigHitP = 6.185 -- Mythic\n            end\n        end\n        \n        \n        -- Empowered Shock Lance\n        local ShockLancedT = select(4, UnitDebuff(\"player\", SpellName(250255))) or 0\n        if ShockLancedT > 0 and UnitIsUnit(\"boss1target\", \"player\") then\n            ShockLanced = 1 + ((ShockLancedT * 15) / 100)\n            if RaidDifficulty == 14 then BigHitP = 3.27      -- Normal\n            elseif RaidDifficulty == 15 then BigHitP = 4.575 -- Heroic\n            elseif RaidDifficulty == 16 then BigHitP = 6.185 -- Mythic\n            end\n        end\n        \n        \n        -- Pulse Grenade (bridge), this takes priorty over shock lance\n        local ShockedT = select(4, UnitDebuff(\"player\", SpellName(250224))) or 0 -- Shocked\n        if ShockedT > 0 then\n            Avoid = 1 - (GetAvoidance() / 100)\n            -- Debuff only exists on Heroic and Mythic\n            if RaidDifficulty == 15 then     -- Heroic\n                Shocked = 1 + ShockedT * 50 / 100\n                BigHitM = 2.3\n            elseif RaidDifficulty == 16 then -- Mythic\n                Shocked = 1 + ShockedT * 75 / 100\n                BigHitM = 3.105\n            end\n        end\n        if UnitDebuff(\"player\", SpellName(254183)) and RaidDifficulty == 15 then Seared = 1.75 -- Seared Skin (Heroic)\n        elseif UnitDebuff(\"player\", SpellName(254183)) and RaidDifficulty == 16 then Seared = 2 end -- Seared Skin (Mythic)\n        BigHitP = BigHitP * ShockLanced * EMPShockLanced * Shocked * Seared\n        BigHitM = BigHitM * ShockLanced * EMPShockLanced * Shocked*  Seared\n    end\n    \n    \n    --- Kin'garoth ---\n    if EncounterID == 2088 then\n        local ForgingStrike = 1\n        SpellNameP = SpellName(254919) -- Forging Strike\n        local ForgingStrikeT = select(4, UnitDebuff(\"player\", SpellName(254919))) or 0\n        if UnitIsUnit(\"boss1target\", \"player\") then\n            Avoid = 1 - (GetAvoidance() / 100)\n            ForgingStrike = 1 + (ForgingStrikeT * 200) / 100\n            if RaidDifficulty == 14 then BigHitP = 7.776      -- Normal\n            elseif RaidDifficulty == 15 then BigHitP = 10.88  -- Heroic\n            elseif RaidDifficulty == 16 then BigHitP = 14.688 -- Mythic\n            end\n        end\n        BigHitP = BigHitP * ForgingStrike\n    end\n    \n    \n    --- Varimathras ---\n    if EncounterID == 2069 then\n        SpellNameM = SpellName(243960) -- Shadow Strike\n        if UnitIsUnit(\"boss1target\", \"player\") then\n            Avoid = 1 - (GetAvoidance() / 100) -- Affected by avoidance\n            if RaidDifficulty == 15 then BigHitM = 4.995    -- Heroic\n            elseif RaidDifficulty == 16 then BigHitM = 12.5 -- Mythic\n            end\n        end\n    end\n    \n    \n    --- The Coven of Shivarra ---\n    if EncounterID == 2073 then\n        local FireStruck = 1\n        SpellNameM = SpellName(244899) -- Fiery Strike\n        local FireStruckT = select(4, UnitDebuff(\"player\", SpellName(244899))) or 0 -- Fiery Strike stacks\n        if FireStruckT > 0 then\n            FireStruck = 1 + (FireStruckT * 100) / 100\n            Avoid = 1 - (GetAvoidance() / 100) -- Affected by avoidance\n            if RaidDifficulty == 14 then BigHitM = 2.5       -- Normal\n            elseif RaidDifficulty == 15 then BigHitM = 3.5   -- Heroic\n            elseif RaidDifficulty == 16 then BigHitM = 4.725 -- Mythic\n            end\n        end\n        BigHitM = BigHitM * FireStruck\n    end\n    \n    \n    --- Aggramar ---\n    if EncounterID == 2063 then\n        local Taeshalach, FoeBreaker, BurningRage = 1, 1, 1\n        -- Taeshalach's Reach and Foe Breaker\n        SpellNameP = SpellName(244291) -- Foe Breaker\n        local now = GetTime()\n        if UnitPower(\"boss1\") > 98 then   -- Start of combo\n            ComboStart = now\n        end\n        -- Shows prediction for 19s after combo start\n        if now - ComboStart < 19 and UnitExists(\"boss1target\") then\n            local BurningRageT = select(4, UnitBuff(\"boss1\", SpellName(244713))) or 0   -- Burning Rage\n            BurningRage = 1 + BurningRageT * 0.5\n            local TaeshalachT = select(4, UnitDebuff(\"player\", SpellName(245990))) or 0 -- Taeshalach's reach\n            Taeshalach = 1 + TaeshalachT * 0.05\n            if UnitDebuff(\"player\", SpellName(244291)) then FoeBreaker = 6 end          -- Foe Breaker\n            Avoid = 1 - (GetAvoidance() / 100)\n            if RaidDifficulty == 14 then BigHitP = 17.85  -- Normal\n            elseif RaidDifficulty == 15 then BigHitP = 25 -- Heroic\n            elseif RaidDifficulty == 16 then BigHitP = 43 -- Mythic\n            end\n        end\n        BigHitP = BigHitP * Taeshalach * FoeBreaker * BurningRage\n    end\n    \n    \n    --- Argus ---\n    if EncounterID == 2092 then\n        local SweepingScythe, DeadlyScythe = 1, 1\n        local SweepingScytheT = select(4, UnitDebuff(\"player\", SpellName(248499))) or 0\n        \n        if SweepingScytheT > 0 and UnitIsUnit(\"boss1target\", \"player\") then -- Sweeping Scythe\n            SpellNameP = SpellName(248499) -- Sweeping Scythe\n            Avoid = 1 - (GetAvoidance() / 100)\n            if RaidDifficulty == 17 then SweepingScythe = 1 + (SweepingScytheT * 50) / 100      -- LFR\n                BigHitP = 3.22\n            elseif RaidDifficulty == 14 then SweepingScythe = 1 + (SweepingScytheT * 75) / 100  -- Normal\n                BigHitP = 4.29\n            elseif RaidDifficulty == 15 then SweepingScythe = 1 + (SweepingScytheT * 150) / 100 -- Heroic\n                BigHitP = 6.01\n            elseif RaidDifficulty == 16 then SweepingScythe = 1 + (SweepingScytheT * 250) / 100 -- Mythic\n                BigHitP = 14\n            end\n        end\n        local DeadlyScytheT = select(4, UnitDebuff(\"player\", SpellName(258039))) or 0 -- Deadly Scythe (Heroic)\n        if DeadlyScytheT > 0 and UnitIsUnit(\"boss1target\", \"player\") then\n            SpellNameP = SpellName(258039) -- Deadly Scythe\n            DeadlyScythe = 1 + (DeadlyScytheT * 200) / 100\n            BigHitP = 6\n        end\n        \n        local SRScytheT = select(4, UnitDebuff(\"player\", SpellName(258838))) or 0 -- Soulrending Scythe (Mythic)\n        if SRScytheT > 0 and UnitIsUnit(\"boss1target\", \"player\") then\n            SpellNameP = SpellName(258838) -- Soulrending Scythe\n            DeadlyScythe = 1 + (SRScytheT * 200) / 100\n            BigHitP = 15\n        end\n        BigHitP = BigHitP * SweepingScythe * DeadlyScythe\n    end\n    \n    -- Physical hit\n    if BigHitP > 0 then\n        ReductionP = ReductionP * Avoid -- Avoidance defaults to 1 if ability is not reduced by avoidance\n        if ClassID == 10 then\n            if UnitBuff(\"player\", SpellName(122278)) then -- Dampen Harm\n                ReductionP = ReductionP / 0.8 / StaggerP -- Remove reductions from the base module\n                -- Reduction scales linearly with size of hit relative to max health\n                local HitSize = ReductionP * BigHitP / MaxHealth\n                if HitSize > 1 then HitSize = 1 end\n                local DampenHarm = 0.8 - 0.3 * HitSize\n                ReductionP = ReductionP * DampenHarm * StaggerP\n            end\n        end\n        if ClassID == 6 then\n            if IsPlayerSpell(211078) then -- Spectral Deflection\n                local Stack = select(4, UnitBuff(\"player\", SpellName(195181))) or 0 -- Bone Shield stacks\n                local HitSize = ReductionP * BigHitP / MaxHealth * 100\n                if Stack > 1 and HitSize > 25 then\n                    ReductionP = ReductionP / 0.84 -- Remove damage reduction from Bone Shield\n                    ReductionP = ReductionP * 0.68 -- Add damage reduction from Spectral Deflection\n                end\n            end\n        end\n        if PImmunity == 1 then BigHitP = 0 end -- check for immunity status\n        AfterHitP = (CurrentHealth + AbsorbsP - BigHitP * ReductionP) / MaxHealth * 100 -- calculates remaining health\n        if AfterHitP > 100 then AfterHitP = 100 end -- set max value for display at 100%\n        -- Apply color and string formatting\n        local ColoredText = \"|cFFFFAF50\" .. SpellNameP .. \"|r:\"\n        AfterHitP = Color(ColoredText, AfterHitP)\n    end\n    \n    \n    -- Magic hit, see above for comments\n    if BigHitM > 0 then\n        ReductionM = ReductionM * Avoid\n        if ClassID == 10 then\n            if UnitBuff(\"player\", SpellName(122278)) then -- Dampen Harm\n                -- Reduction scales linearly with size of hit relative to max health\n                ReductionM = ReductionM / 0.8 / StaggerM -- Remove reductions from the base module\n                local HitSize = ReductionM * BigHitM / MaxHealth\n                if HitSize > 1 then HitSize = 1 end\n                local DampenHarm = 0.8 - 0.3 * HitSize\n                ReductionM = ReductionM * DampenHarm * StaggerM\n            end\n        end\n        if ClassID == 6 then\n            if IsPlayerSpell(211078) then -- Spectral Deflection\n                local Stack = select(4, UnitBuff(\"player\", SpellName(195181))) or 0 -- Bone Shield stacks\n                local HitSize = ReductionM * BigHitM / MaxHealth * 100\n                if Stack > 1 and HitSize > 25 then\n                    ReductionM = ReductionM / 0.84 -- Remove damage reduction from Bone Shield\n                    ReductionM = ReductionM * 0.68 -- Add damage reduction from Spectral Deflection\n                end\n            end\n        end\n        if MImmunity == 1 then BigHitM = 0 end\n        AfterHitM = (CurrentHealth + AbsorbsM - BigHitM * ReductionM) / MaxHealth * 100\n        if AfterHitM > 100 then AfterHitM = 100 end\n        local ColoredText = \"|cFF197DFF\" .. SpellNameM .. \"|r:\"\n        AfterHitM = Color(ColoredText, AfterHitM)\n    end\n    \n    local NewLine = \"\"\n    if BigHitP and BigHitM > 0 then NewLine = \"\\n\" end\n    \n    return AfterHitP .. NewLine .. AfterHitM\nend",
