				["init"] = {
					["custom"] = "-- Made by Zeler-Ravencrest EU, with great help from Stryer-Ravencrest EU\nlocal decimals = \"0\" -- The number of decimals displayed\n\nlocal EndText = \"|c%s%.\" .. decimals .. \"f%%|r\"\n\naura_env.RaidDifficulty = GetRaidDifficultyID()\naura_env.EncounterID = 0\naura_env.ClassID = select(3, UnitClass(\"player\"))\n\n-- Aggramar\naura_env.ComboStart = 0\n\n-- Color values, the accepted values are from 0 to 255\n-- The 1st value is for red, 2nd is for green and 3rd is for blue\nlocal ColorI = {0, 60, 255}  -- The color if you have an immunity active\nlocal Color1 = {255, 0, 0}   -- The color if you will have < 6% health left\nlocal Color2 = {255, 128, 0} -- The color if you will have >= 6% health left\nlocal Color3 = {0, 255, 0}   -- The color if you will have => 20% health left\n\nlocal function RGBtoHex(rgb)\n    return string.format(\"ff%02x%02x%02x\", rgb[1], rgb[2], rgb[3])\nend\n\nlocal ColorI = RGBtoHex(ColorI)\nlocal Color1 = RGBtoHex(Color1)\nlocal Color2 = RGBtoHex(Color2)\nlocal Color3 = RGBtoHex(Color3)\n\n-- Returns formatted health based on percentage\nfunction aura_env.Color(ColoredText, Health)\n    if Health >= 20 then return string.format(ColoredText .. EndText, Color3, Health)\n    elseif Health >= 6 then return string.format(ColoredText .. EndText, Color2, Health)\n    elseif Health < 6 then return string.format(ColoredText .. EndText, Color1, Health)\n    end\nend\n\nlocal spell = {}\naura_env.SpellName = function(spellid)\n    spell[spellid] = spell[spellid] or (GetSpellInfo(spellid))\n    return spell[spellid]\nend",
